mapper.select:
SELECT T.ID, T.USER_NO, T.PASS, T.USER_NAME, T.MOBILE_NO, T.USER_ADD, T.USER_AGE, T.USER_BIRTH, T.USER_TYPE, CASE T.USER_TYPE WHEN 1 THEN '管理员' WHEN 2 THEN '普通用户' ELSE T.USER_TYPE END USER_TYPE_NAME, T.STATUS, T.BANK_NO, T.BANK_NAME, T.REAL_NAME, T.WX_NO, T.ZFB_NO, T.WX_FILE_URL, T.ZFB_FILE_URL, DATE_FORMAT(T.CRITE_TIME,'%Y-%m-%d %H:%i:%s') CRITE_TIME, T.CRITE_USER,USR.USER_NAME CRITENM FROM T_DITY_USER T LEFT JOIN T_DITY_USER USR ON T.CRITE_USER = USR.USER_NO WHERE 1=1
AND T.ID = #{ID}
AND T.USER_NAME like concat('%',#{USER_NAME},'%')
AND T.USER_NO like concat('%',#{USER_NO},'%')
AND T.USER_TYPE = #{USER_TYPE}
ORDER BY T.USER_TYPE

SELECT ID, USER_NO, PASS, USER_NAME, MOBILE_NO, USER_AGE, USER_BIRTH, USER_TYPE, CASE USER_TYPE WHEN 1 THEN '管理员' WHEN 2 THEN '普通用户' ELSE USER_TYPE END USER_TYPE_NAME, STATUS, BANK_NO, BANK_NAME, REAL_NAME, WX_NO, ZFB_NO, WX_FILE_URL, ZFB_FILE_URL, DATE_FORMAT(CRITE_TIME,'%Y-%m-%d %H:%i:%s') CRITE_TIME, CRITE_USER FROM T_DITY_USER T where 1=1
and ID != #{ID}
and USER_NO = #{USER_NO}

mapper.update:
UPDATE T_DITY_USER T SET 
		T.ID = #{ID}

		, T.USER_NO = #{USER_NO}


		, T.PASS = #{PASS}


		, T.USER_NAME = #{USER_NAME}


		, T.MOBILE_NO = #{MOBILE_NO}


		, T.USER_ADD = #{USER_ADD}


		, T.USER_AGE = #{USER_AGE}


		, T.USER_BIRTH = #{USER_BIRTH}


		, T.USER_TYPE = #{USER_TYPE}


		, T.BANK_NO = #{BANK_NO}


		, T.BANK_NAME = #{BANK_NAME}


		, T.REAL_NAME = #{REAL_NAME}


		, T.WX_NO = #{WX_NO}


		, T.ZFB_NO = #{ZFB_NO}


		, T.WX_FILE_URL = #{WX_FILE_URL}


		, T.ZFB_FILE_URL = #{ZFB_FILE_URL}


		, T.TX_FILE_URL = #{TX_FILE_URL}


		, T.STATUS = #{STATUS}

	WHERE T.ID = #{ID}

UPDATE T_DITY_ROTTN_CHRT SET 
		IMAGE_ORDER = #{IMAGE_ORDER}
	WHERE ID = #{ID}

mapper.select:
SELECT ((select t.RATE_VAL from t_dity_rate t where t.RATE_NAME='委托费') * t.PRICE) WTF, ((select t.RATE_VAL from t_dity_rate t where t.RATE_NAME='预计收益') * t.PRICE) YJSY, t.PRICE, t.*, USR.USER_NAME OWN_ACNT_NAME, CASE t.STATUS WHEN 0 THEN '已下架' WHEN 1 THEN '正常' ELSE t.STATUS END STATUS_NAME, DATE_FORMAT(t.CRITE_TIME,'%Y-%m-%d %H:%i:%s') CRITE_TIME FROM T_DITY_GOODS_MS T LEFT JOIN T_DITY_USER USR ON T.OWN_ACNT = USR.USER_NO WHERE 1 = 1
and t.ID = #{ID}
and t.NAME like concat('%',#{NAME},'%')

mapper.update:
UPDATE T_DITY_GOODS_MS T SET 
		T.STATUS = #{STATUS}
	WHERE T.ID = #{ID}

mapper.select:
SELECT T.*, ((select t.RATE_VAL from t_dity_rate t where t.RATE_NAME='委托费') * t.PRICE) WTF, ((select t.RATE_VAL from t_dity_rate t where t.RATE_NAME='预计收益') * t.PRICE) YJSY, TYPE.TYPE_NAME, USR.USER_NAME OWN_ACNT_NAME, CASE T.STATUS WHEN 0 THEN '已下架' WHEN 1 THEN '正常' ELSE T.STATUS END STATUS_NAME, DATE_FORMAT(T.CRITE_TIME,'%Y-%m-%d %H:%i:%s') CRITE_TIME FROM T_DITY_GOODS T LEFT JOIN t_dity_product_type TYPE ON T.GOODS_TYPE = TYPE.ID LEFT JOIN T_DITY_USER USR ON T.OWN_ACNT = USR.USER_NO WHERE 1 = 1
and t.ID = #{ID}
and t.NAME like concat('%',#{NAME},'%')
and t.GOODS_TYPE = #{GOODS_TYPE}

mapper.update:
UPDATE T_DITY_GOODS T SET 
		T.STATUS = #{STATUS}
	WHERE T.ID = #{ID}

mapper.select:
SELECT * FROM T_DITY_USERDZ 

	where ID = #{ID}


	where USER_NO = #{USER_NO}

mapper.update:
UPDATE T_DITY_USERDZ T SET 
		T.ISMOREN = 1
	WHERE T.ID = #{ID}

UPDATE T_DITY_USERDZ T SET 
		T.ISMOREN = 0
	WHERE T.USER_NO = #{USER_NO}

mapper.select:
SELECT t.ID, t.ORDER_NO, t.ORDER_PD, t.ORDER_PD_NAME, t.ORDER_PD_TYPE, CASE T.ORDER_PD_TYPE WHEN 0 THEN '非秒杀商品订单' WHEN 1 THEN '秒杀商品订单' ELSE T.ORDER_PD_TYPE END ORDER_PD_TYPE_NAME, t.ORDER_PRICE, t.ORDER_CVAL, t.ORDER_CFEE, t.ORDER_INCOM, t.ORDER_EXPRESS, t.ORDER_USER_SEL_NO, u.USER_NAME ORDER_USER_SEL_NAME, t.ORDER_USER_BUY_NO, uu.USER_NAME ORDER_USER_BUY_NAME, t.STATUS, CASE T.STATUS WHEN 0 THEN '买家未确认付款' WHEN 1 THEN '买家已确认付款' WHEN 2 THEN '卖家已确认收款' WHEN 3 THEN '卖家已发货' WHEN 4 THEN '买家已发起委托，待管理员确认委托' WHEN 5 THEN '管理员已确认委托' ELSE T.STATUS END STATUS_NAME, DATE_FORMAT(t.OPTION_TIME,'%Y-%m-%d %H:%i:%s') OPTION_TIME, t.OPTION_USER FROM t_dity_order t left join t_dity_user u on t.ORDER_USER_SEL_NO=u.USER_NO left join t_dity_user uu on t.ORDER_USER_BUY_NO=uu.USER_NO where 1=1
and T.ID = #{ID}
and T.STATUS = 0 and T.ORDER_USER_BUY_NO = #{USER_NO}
and T.STATUS = 1 and T.ORDER_USER_SEL_NO = #{USER_NO}
and T.STATUS = 3 and T.ORDER_USER_BUY_NO = #{USER_NO}
and T.STATUS = 2 and T.ORDER_USER_BUY_NO = #{USER_NO}
and T.STATUS != 1 and T.ORDER_USER_SEL_NO = #{USER_NO}
and T.ORDER_PD = #{ORDER_PD}
and T.STATUS = #{STATUS}
order by T.OPTION_TIME desc

mapper.update:
UPDATE t_dity_order T SET 
		T.STATUS = #{STATUS}
	WHERE T.ID = #{ID}

update T_DITY_GOODS_MS t set 
	t.OWN_ACNT=(select a.ORDER_USER_BUY_NO 
		from t_dity_order a where a.id=#{ORDERID})

update T_DITY_GOODS t set 
	t.OWN_ACNT=(select a.ORDER_USER_BUY_NO 
		from t_dity_order a where a.id=#{ORDERID})

UPDATE t_dity_order T SET 
		T.STATUS = #{STATUS}
		,T.ORDER_EXPRESS = #{ORDER_EXPRESS}
	WHERE T.ID = #{ID}
